@article{Kogler2025,
abstract = {We examine how firm capabilities and regional knowledge bases co-evolve by studying Nortel, a major telecommunications manufacturer, in its home region of Ottawa–Gatineau and in the regions it rapidly entered during the late-1990s dot- com boom. We introduce a patent-based measure of technological similarity be- tween firms and regions to operationalize their potential compatibility and embed it in a “knowledge-space” mapping approach. Across locations, inventive produc- tivity, proxied by patent output, is higher where Nortel's technological profile is most similar to the host region's specialization. Technological similarity is a strong predictor of Nortel's local patenting rates and, during corporate decline, greater technological embeddedness is associated with slower local exit. These results dem- onstrate that aligning firm capabilities with regional strengths materially influences innovative performance and location dynamics. The proposed knowledge-space tool offers a replicable method for business location decisions and for regional policy seeking to attract, retain, and upgrade technology-intensive activity.},
author = {Kogler, Dieter F and Kedron, Peter and Kim, Keungoui},
file = {:C\:/Users/user/Downloads/268e5fdb-d759-4936-8b67-b3454c986b6c.pdf:pdf;:C\:/Users/user/AppData/Local/Mendeley Ltd./Mendeley Desktop/Downloaded/Kogler, Kedron, Kim - 2025 - The firm-location nexus examining inventive footprint and technological evolution of Nortel over the course.pdf:pdf},
journal = {Evolutionary and Institutional Economic Review},
keywords = {Firm–region co-evolution,Inventive productivity,Knowledge space,Nortel,Ottawa–Gatineau,Patent data,Technological embeddedness,Technological similarity},
title = {{The firm-location nexus: examining inventive footprint and technological evolution of Nortel over the course of its "boom and bust"}},
year = {2025}
}

